import Paragraph from './components/Paragraph'
import About from "./components/About";
import SliderComp from "./components/SliderComp";
import Header from './components/Header'
import Image from './components/Image'
import Button from './components/Button'
import Footer from './components/Footer'
import {nanoid} from 'nanoid'
import {useState} from 'react'


function App() {

    const onPrint = (el) => {
        // window.print()
        const  w = window.open();
        w.document.write('<html><head><title></title>');
        w.document.write('<link rel="stylesheet" media="screen" href="print.css" />');
        w.document.write('</head><body >');
        w.document.write(document.getElementById(el).innerHTML);
        w.document.write('</body></html>');

        w.document.close();
        w.focus();
        w.print();
        w.close();
        return true;
    }
  return (
    <div className="container">
        <div className="header">
            <Image/>
            <Button/>
        </div>
        {/*<div className="main">*/}
        {/*    /!*<Header  title='About'/>*!/*/}
        {/*    /!* <About/>*!/*/}
        {/*</div>*/}

       <div className="aside">
       <div>
           <Header  title='About'/>
           <About/>
       </div>
           <div><Header  title='Goals'/>
               <Paragraph />
           </div>
           <div>
               <Header  title='Needs'/>
               <Paragraph />
           </div>
          <div> <Header  title='Pain Points'/>
              <Paragraph />
          </div>
           <div>
               <Header  title='Personality'/>
               <SliderComp/>
           </div>
       </div>
        <button className="footer-btn" onClick={onPrint}  >Preview</button>

    </div>
  );
}

export default App;
