import React from 'react'
import styled from 'styled-components'

export const BASE_HEIGHT: number = 19

export const Container = styled(({ height, ...rest }) => {
  return <div {...rest} />
})`
  height: ${(props) => props.height}px;
  display: flex;
  flex-direction: row;
  margin-bottom: 40px;
`

export const TextArea = styled.textarea`
  line-height: 1.1876em;
  height: 100%;
  width: 100%;
  resize: none;
  padding: 18.5px;
  padding-left: 35px;
  padding-right: 14px;
  align-items: 'center';
  font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
  color: rgba(0, 0, 0, 0.87);
  border-color: rgba(0, 0, 0, 0.23);
  border-radius: 4px;
  font-size: 1rem;
  letter-spacing: 0.00938em;
  white-space: nowrap;
  outline: none;
  overflow-y: hidden;

  ::-webkit-scrollbar {
    height: 5px;
  }

  ::-webkit-scrollbar-track {
    width: 5px;
    background: #f1f1f1;
  }

  ::-webkit-scrollbar-thumb {
    width: 5px;
    background: rgba(0, 0, 0, 0.23);
  }

  ::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, 0.5);
  }
`

export const BulletContainer = styled.div`
  position: absolute;
  padding: 18.5px 14px;
  background-color: white;
  border-top: 1px solid rgba(0, 0, 0, 0.23);
  border-left: 1px solid rgba(0, 0, 0, 0.23);
  border-radius: 4px 0px 0px 4px;
`

export const Bullet = styled.p`
  margin: 0;
  line-height: 1.1876em;
  color: rgba(0, 0, 0, 0.5);
`
import React, { useCallback, useEffect, useMemo, useState } from 'react'
import {
  TextArea,
  Container,
  Bullet,
  BulletContainer,
  BASE_HEIGHT
} from '../../styles'

export interface BulletedTextAreaProps {
  onChange: (values: string[]) => void
  values: string[]
  bulletChar?: string
}

const BulletedTextArea: React.FC<BulletedTextAreaProps> = (props) => {
  const { bulletChar, onChange, values, ...rest } = props
  const [lines, setLines] = useState(values.length > 0 ? values.length : 1)
  const [value, setValue] = useState(values.join('\n'))

  useEffect(() => {
    setValue(values.join('\n'))
  }, [values])

  const handleChange = useCallback(
    (e) => {
      const newValue = e.target.value
      const values = newValue.split('\n')
      const curLines = values.length
      if (curLines !== lines) {
        setLines(curLines)
      }
      setValue(newValue)
      // split the values and return array
      onChange(values)
    },
    [setValue, setLines, lines, onChange]
  )

  const handleKeyPress = useCallback((e) => {
    // when enter pressed, reset scroll to left
    if (e.charCode === 13) {
      e.target.scrollLeft = 0
    }
  }, [])

  return useMemo(() => {
    const height = BASE_HEIGHT * lines
    const bullets = new Array(lines).fill(bulletChar)
    return (
      <Container height={height} {...rest}>
        <BulletContainer>
          {bullets.map((bullet, i) => (
            <Bullet key={`bullet-${i}`}>{bullet}</Bullet>
          ))}
        </BulletContainer>
        <TextArea
          value={value}
          onChange={handleChange}
          onKeyPress={handleKeyPress}
        />
      </Container>
    )
  }, [value, lines, handleChange])
}

BulletedTextArea.defaultProps = {
  bulletChar: '-',
  values: []
}

export default BulletedTextArea
import React from 'react'
import InputAdornment from '@material-ui/core/InputAdornment'
import styled from 'styled-components'
import { Bullet } from '../../../styles'

const StyledAdornment = styled(InputAdornment)`
  flex-direction: column;
  max-height: unset;
  height: unset;
`

export interface BulletAdornmentProps {
  bulletChar?: string
  lines: number
}

const BulletAdornment: React.FC<BulletAdornmentProps> = (props) => {
  const { bulletChar, lines } = props
  const bullets = new Array(lines).fill(bulletChar)
  return (
    <StyledAdornment position='start'>
      {bullets.map((bullet, i) => (
        <Bullet key={`bullet-${i}`}>{bullet}</Bullet>
      ))}
    </StyledAdornment>
  )
}

BulletAdornment.defaultProps = {
  bulletChar: '-'
}

export default BulletAdornment
import React, { useCallback, useEffect, useState } from 'react'
import TextField from '@material-ui/core/TextField'
import BulletAdornment from './BulletAdornment'

interface MuiBulletedTextAreaProps {
  onChange: (values: string[]) => void
  values: string[]
  bulletChar?: string
}

const MuiBulletedTextArea: React.FC<MuiBulletedTextAreaProps> = (props) => {
  const { bulletChar, onChange, values, ...rest } = props
  const [lines, setLines] = useState(values.length > 0 ? values.length : 1)
  const [value, setValue] = useState(values.join('\n'))

  const handleChange = useCallback(
    (e) => {
      const newValue = e.target.value
      const values = newValue.split('\n')
      const curLines = values.length
      if (curLines !== lines) {
        setLines(curLines)
      }
      setValue(newValue)
      // split the values and return array
      onChange(values)
    },
    [setValue, setLines, lines, onChange]
  )

  useEffect(() => {
    setValue(values.join('\n'))
  }, [values, setValue])

  return (
    <TextField
      variant='outlined'
      multiline
      InputProps={{
        startAdornment: (
          <BulletAdornment bulletChar={bulletChar} lines={lines} />
        )
      }}
      onChange={handleChange}
      value={value}
      {...rest}
    />
  )
}

MuiBulletedTextArea.defaultProps = {
  values: []
}

export default MuiBulletedTextArea
